d-gap: 0px;\r\n}\r\n.temp-grid-item-3[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  width: 25%;\r\n}\r\n.temp-grid-item-4[_ngcontent-%COMP%] {\r\n  display: flex;\r\n  align-items: center;\r\n  grid-template-columns: max-content max-content;\r\n  grid-gap: 0px;\r\n}\r\n.temp-grid-item-4[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  width: 25%;\r\n}\r\n.select-box[_ngcontent-%COMP%]   select[_ngcontent-%COMP%], .select-box-2[_ngcontent-%COMP%]   select[_ngcontent-%COMP%] {\r\n  height: 100%;\r\n  width: 100%;\r\n  outline: none;\r\n  border: none;\r\n  color: #707070;\r\n  font-size: 1rem;\r\n}\r\n\r\n.select-box[_ngcontent-%COMP%]   .ng-dirty[_ngcontent-%COMP%], .select-box-2[_ngcontent-%COMP%]   .ng-dirty[_ngcontent-%COMP%] {\r\n  color: white;\r\n}\r\n\r\n.fixed-text[_ngcontent-%COMP%] {\r\n  display: flex;\r\n  align-items: center;\r\n  grid-template-columns: max-content max-content;\r\n  grid-gap: 6%;\r\n  width: 96%;\r\n}\r\n.fixed-text[_ngcontent-%COMP%]   .label[_ngcontent-%COMP%] {\r\n  margin: 0.5%;\r\n}\r\nbutton[_ngcontent-%COMP%] {\r\n  height: 40px;\r\n  width: 10%;\r\n  margin: 3px;\r\n  color: #eceff1;\r\n  font-size: 1rem;\r\n  font-weight: 400;\r\n  cursor: pointer;\r\n  transition: all 0.2s ease;\r\n  background-color: #455a64;\r\n  border: 1px solid #ddd;\r\n  border-radius: 6px;\r\n  padding: 0 15px;\r\n}\r\n\r\nbutton[_ngcontent-%COMP%]:hover, button[_ngcontent-%COMP%]:focus {\r\n  background-color: #78909c;\r\n}\r\n.broadcast[_ngcontent-%COMP%] {\r\n  float: right;\r\n}\r\n\r\n.broadcast-mode-buttons[_ngcontent-%COMP%] {\r\n  margin-top: 0.5%;\r\n}\r\n\r\nselect[_ngcontent-%COMP%]   option[_ngcontent-%COMP%]:checked {\r\n  color: white;\r\n  background-color: #000080;\r\n}\r\nselect[_ngcontent-%COMP%] {\r\n  color: black;\r\n  background-color: white;\r\n}\r\n\r\n.select-box[_ngcontent-%COMP%]   .selected[_ngcontent-%COMP%] {\r\n  background-color: #000080;\r\n}\r\ninput[readonly][_ngcontent-%COMP%], textarea[readonly][_ngcontent-%COMP%] {\r\n  background-color: #747474 !important;\r\n  color: #dbdbdb !important;\r\n}\r\n.readonly-select[_ngcontent-%COMP%] {\r\n  background-color: #707070 !important;\r\n  color: white !important;\r\n}\r\n.disabled-button[_ngcontent-%COMP%] {\r\n  background-color: gray;\r\n  color: white;\r\n  cursor: not-allowed;\r\n}\r\n.select-box[_ngcontent-%COMP%]:has(select[style=\"background-color: rgb(0, 0, 128);\"]) {\r\n  background-color: #000080;\r\n}\r\nselect[style=\"background-color: rgb(0, 0, 128);\"][_ngcontent-%COMP%] {\r\n  color: white !important;\r\n}\r\n\r\n@media screen and (max-width: 500px) {\r\n  .column[_ngcontent-%COMP%] {\r\n    flex-wrap: wrap;\r\n  }\r\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
