import { formatDate } from '@angular/common';
import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-tabs-evra',
  templateUrl: './tabs-evra.component.html',
  styleUrls: ['./tabs-evra.component.css'],
})
export class TabsEvraComponent {
  messageEnglish: string;
  nextMessageEnglish: string;
  messageES: string;
  nextMessageES: string;
  messageDATIS: string;
  nextMessageDATIS: string;
  state: boolean;
  broadcastTime: string;
  currentCode: string;
  nextCode: string;
  isRed: boolean = false;

  constructor(private http: HttpClient) {}
  getCurrentCode(currentCode) {
    this.currentCode = currentCode;
    this.addClass();
  }
  getNextCode(nextCode) {
    this.nextCode = nextCode;
  }

  getBroadcastTime(broadcastTime) {
    this.broadcastTime = formatDate(broadcastTime, 'HH:mm', 'en-US', '+0000');
  }
  getCurrentBroadcastEN(messageEnglish) {
    this.messageEnglish = messageEnglish;
  }
  getCurrentBroadcastES(messageES) {
    this.messageES = messageES;
  }
  getCurrentBroadcastDATIS(messageDATIS) {
    this.messageDATIS = messageDATIS;
  }
  getSubscribeState(state) {
    this.state = state;
  }
  getNextBroadcastEN(nextMessageEnglish) {
    this.nextMessageEnglish = nextMessageEnglish;
  }
  getNextBroadcastES(nextMessageES) {
    this.nextMessageES = nextMessageES;
  }
  getNextBroadcastDATIS(nextMessageDATIS) {
    this.nextMessageDATIS = nextMessageDATIS;
  }
  addClass() {
    if (this.currentCode === 'N/A') {
      this.isRed = true;
    } else {
      this.isRed = false;
    }
  }

  convertTextToSpeech(message: string, language: string): void {
    const requestBody = { text: message, language: language };
    console.log(message);
    this.http
      .post<any>('http://localhost:5000/convert-text-to-speech', requestBody)
      .subscribe({
        next: (response) => {
          const audio = new Audio(
            'http://localhost:5000/assets/current-broadcast.mp3?_=' + Date.now()
          );
          audio.play();
        },
        error: (error) => {
          // Handle any errors
        },
      });
  }
}
<link
  rel="stylesheet"
  href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
/>
<div class="outter-grid">
  <div class="grid">
    <div class="container-title">
      <span title="Riga International Airport" class="title">EVRA</span>
      <div [ngClass]="state ? 'led-green' : 'led-red'"></div>
    </div>
    <p class="lastBroadcast">Last broadcast: {{ broadcastTime }}</p>
  </div>
  <div class="current">
    <p class="atis-code" [class.red]="isRed">({{ currentCode }}) (Current)</p>

    <button
      class="icon-button"
      (click)="convertTextToSpeech(messageEnglish, 'en')"
    >
      <i class="fa fa-volume-up fa-2x" aria-hidden="true"></i>
    </button>
  </div>
  <div class="next">
    <p class="atis-code">({{ nextCode }}) (Next)</p>
    <button
      class="icon-button"
      (click)="convertTextToSpeech(nextMessageEnglish, 'en')"
    >
      <i class="fa fa-volume-up fa-2x" aria-hidden="true"></i>
    </button>
  </div>
</div>
<mat-tab-group class="custom-tab-group">
  <mat-tab>
    <ng-template mat-tab-label>
      <span class="tabName">ATIS Report </span>
    </ng-template>
    <div class="container">
      <app-atis-form-evra
        (lastBroadcastTime)="getBroadcastTime($event)"
        (isSubscribed)="getSubscribeState($event)"
        (currentBroadcastEnglish)="getCurrentBroadcastEN($event)"
        (nextBroadcastEnglish)="getNextBroadcastEN($event)"
        (currentBroadcastSpanish)="getCurrentBroadcastES($event)"
        (nextBroadcastSpanish)="getNextBroadcastES($event)"
        (currentBroadcastDATIS)="getCurrentBroadcastDATIS($event)"
        (nextBroadcastDATIS)="getNextBroadcastDATIS($event)"
        (currentAtisCode)="getCurrentCode($event)"
        (nextAtisCode)="getNextCode($event)"
      ></app-atis-form-evra>
    </div>
  </mat-tab>
  <mat-tab>
    <ng-template mat-tab-label>
      <span class="tabName">Overview English </span>
    </ng-template>
    <div class="container">
      <app-overview
        [currentBroadcastEN]="messageEnglish"
        [nextBroadcastEN]="nextMessageEnglish"
      ></app-overview>
    </div>
  </mat-tab>
  <mat-tab>
    <ng-template mat-tab-label>
      <span class="tabName">DATIS </span>
    </ng-template>
    <div class="container">
      <app-overview-datis
        [currentBroadcastDATIS]="messageDATIS"
        [nextBroadcastDATIS]="nextMessageDATIS"
      ></app-overview-datis>
    </div>
  </mat-tab>
</mat-tab-group>
