  margin-left: -470%;\r\n}\r\n.grid-item-1[_ngcontent-%COMP%]   label[_ngcontent-%COMP%], .grid-item-2[_ngcontent-%COMP%]   label[_ngcontent-%COMP%], .grid-item[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  display: flex;\r\n  align-items: center;\r\n}\r\n.grid-item-2[_ngcontent-%COMP%]   input[_ngcontent-%COMP%] {\r\n  width: 100%;\r\n}\r\n.grid-item-2[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  text-align: center;\r\n}\r\n.grid-item[_ngcontent-%COMP%] {\r\n  display: flex;\r\n  align-items: center;\r\n}\r\n\r\n.select-box[_ngcontent-%COMP%]   select[_ngcontent-%COMP%], .select-box-2[_ngcontent-%COMP%]   select[_ngcontent-%COMP%] {\r\n  height: 100%;\r\n  width: 100%;\r\n  outline: none;\r\n  border: none;\r\n  color: #707070;\r\n  font-size: 1rem;\r\n}\r\n\r\n.select-box[_ngcontent-%COMP%]   .ng-dirty[_ngcontent-%COMP%], .select-box-2[_ngcontent-%COMP%]   .ng-dirty[_ngcontent-%COMP%] {\r\n  color: white;\r\n}\r\n.rvr-grid[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: repeat(3, 3fr);\r\n  grid-column-gap: 0px;\r\n  grid-row-gap: 0px;\r\n}\r\n.rvr-grid[_ngcontent-%COMP%]   .input-box[_ngcontent-%COMP%] {\r\n  width: 50%;\r\n}\r\n.rvr-grid[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  width: 82%;\r\n}\r\nbutton[_ngcontent-%COMP%] {\r\n  height: 40px;\r\n  width: 10%;\r\n  margin: 3px;\r\n  color: #eceff1;\r\n  font-size: 1rem;\r\n  font-weight: 400;\r\n  cursor: pointer;\r\n  transition: all 0.2s ease;\r\n  background-color: #455a64;\r\n  border: 1px solid #ddd;\r\n  border-radius: 6px;\r\n  padding: 0 15px;\r\n}\r\n\r\nbutton[_ngcontent-%COMP%]:hover, button[_ngcontent-%COMP%]:focus {\r\n  background-color: #78909c;\r\n}\r\n.broadcast[_ngcontent-%COMP%] {\r\n  float: right;\r\n}\r\n\r\n.broadcast-mode-buttons[_ngcontent-%COMP%] {\r\n  margin-top: 0.2%;\r\n  margin-bottom: -0.85%;\r\n}\r\n\r\nselect[_ngcontent-%COMP%]   option[_ngcontent-%COMP%]:checked {\r\n  color: white;\r\n  background-color: #000080;\r\n}\r\nselect[_ngcontent-%COMP%] {\r\n  color: black;\r\n  background-color: white;\r\n}\r\n\r\n.select-box[_ngcontent-%COMP%]   .selected[_ngcontent-%COMP%] {\r\n  background-color: #000080;\r\n}\r\ninput[readonly][_ngcontent-%COMP%], textarea[readonly][_ngcontent-%COMP%] {\r\n  background-color: #747474 !important;\r\n  color: #dbdbdb !important;\r\n}\r\n.readonly-select[_ngcontent-%COMP%] {\r\n  background-color: #707070 !important;\r\n  color: white !important;\r\n}\r\n.disabled-button[_ngcontent-%COMP%] {\r\n  background-color: gray;\r\n  color: white;\r\n  cursor: not-allowed;\r\n}\r\n.typeapp[_ngcontent-%COMP%] {\r\n  width: 55%;\r\n}\r\n.wind-shear-grid[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: 6.5fr 5fr 4fr 6fr 1fr;\r\n  grid-template-rows: 1fr;\r\n  grid-row-gap: 0px;\r\n  grid-column-gap: 2px;\r\n}\r\n\r\n.wind-shear-grid[_ngcontent-%COMP%]   .wind-shear[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: 1.17fr 2fr;\r\n  grid-template-rows: 1fr;\r\n  grid-row-gap: 0px;\r\n  grid-column-gap: 2px;\r\n}\r\n\r\n.wind-shear-grid[_ngcontent-%COMP%]   .rep[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: 2.15fr 1fr;\r\n}\r\n.rep[_ngcontent-%COMP%]   .select-box[_ngcontent-%COMP%] {\r\n  width: 86%;\r\n  margin-left: 1px;\r\n}\r\n.rep[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  left: 60px;\r\n  position: relative;\r\n}\r\n\r\n.wind-shear-grid[_ngcontent-%COMP%]   .ws[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: 1fr 0.9fr;\r\n}\r\n\r\n.wind-shear[_ngcontent-%COMP%]   .select-box[_ngcontent-%COMP%] {\r\n  width: 100%;\r\n}\r\n\r\n.turbulence-grid[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: repeat(2, 1fr);\r\n  grid-template-rows: 1fr;\r\n  grid-row-gap: 0px;\r\n}\r\n.turbulence-grid[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  margin-right: 10%;\r\n}\r\n\r\n@media screen and (max-width: 500px) {\r\n  .column[_ngcontent-%COMP%] {\r\