          : isChanged['windsheartime'] == 2
          ? '#707070'
          : '#DC143C'
      "
      [style.caret-color]="isChanged['windsheartime'] == 0 ? 'black' : 'white'"
      [readonly]="readonlyMode"
      [class.readonly-input]="readonlyMode"
      (keyup)="onInputChange($event)"
    />
  </div>
</div>
<div class="turbulence-grid">
  <div class="grid-item">
    <label>Turbulence</label>
    <div class="select-box typeapp">
      <select
        [(ngModel)]="formAtisFields['turbulenceintensity'].value"
        (ngModelChange)="onInputChange($event, 'turbulenceintensity')"
        name="turbulenceintensity"
        [style.background-color]="
          isChanged['turbulenceintensity'] == 0
            ? 'white'
            : isChanged['turbulenceintensity'] == 1
            ? '#000080'
            : isChanged['turbulenceintensity'] == 2
            ? '#707070'
            : '#DC143C'
        "
        [disabled]="readonlyMode"
        [ngClass]="{ 'readonly-select': readonlyMode }"
      >
        <option></option>
        <option>MODERATE</option>
        <option>SEVERE</option>
      </select>
    </div>
  </div>
  <div class="grid-item">
    <label>TURB</label>
    <div class="select-box typeapp">
      <select
        [(ngModel)]="formAtisFields['turbulencelocation'].value"
        (ngModelChange)="onInputChange($event, 'turbulencelocation')"
        name="turbulencelocation"
        [style.background-color]="
          isChanged['turbulencelocation'] == 0
            ? 'white'
            : isChanged['turbulencelocation'] == 1
            ? '#000080'
            : isChanged['turbulencelocation'] == 2
            ? '#707070'
            : '#DC143C'
        "
        [disabled]="readonlyMode"
        [ngClass]="{ 'readonly-select': readonlyMode }"
      >
        <option></option>
        <option>IN_CLOUDS</option>
        <option>BETWEEN ... FEET AND ... FEET</option>
        <option>FROM GROUND TO ... FEET</option>
      </select>
    </div>
  </div>
</div>
<div class="broadcast-mode-buttons">
  <label>Broadcast mode</label>
  <br />
  <button (click)="manualMode()">Manual</button>
  <button (click)="semiAutoMode()">SemiAuto</button>
  <button (click)="autoMode()">Auto</button>
  <button
    class="broadcast"
    (click)="sendBroadcastMessage()"
    [disabled]="isBroadcastBtnDisabled"
    [ngClass]="{ 'disabled-button': isBroadcastBtnDisabled }"
  >
    Broadcast
  </button>
</div>
