import { Component, OnInit } from '@angular/core';
import { CalendarEvent, CalendarView } from 'angular-calendar';
import { MonthViewDay } from 'calendar-utils';
import { CalendarService } from '../_services/calendar.service';


@Component({
  selector: 'app-calendar',
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.css']
})
export class CalendarComponent implements OnInit {
  view: CalendarView = CalendarView.Month;
  viewDate: Date = new Date();
  events: CalendarEvent[] = [];

  constructor(private calendarService: CalendarService) { }

  ngOnInit(): void {
    // Load events from the service when the component initializes
    this.loadEvents();
  }

  loadEvents(): void {
    // Use the calendar service to get events
    this.calendarService.getEvents().subscribe((events) => {
      this.events = events;
    });
  }
  dayClicked({ day }: { day: MonthViewDay<any> }): void {
    // Handle day click events here
    console.log('Day clicked', day.date);
  }

  eventClicked({ event }: { event: CalendarEvent }): void {
    // Handle event click events here
    console.log('Event clicked', event);
  }
}
