import { Component } from '@angular/core';
import {
  trigger,
  state,
  style,
  transition,
  animate,
} from '@angular/animations';
import { StorageService } from '../_services/storage.service';
import { LogService } from '../_services/log.service';
import { AuthService } from '../_services/auth.service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-root',
  templateUrl: './settings.component.html',
  styleUrls: ['./settings.component.css'],
  animations: [
    trigger('slideInOut', [
      state(
        'in',
        style({
          transform: 'translateY(0)',
          opacity: 1,
        })
      ),
      state(
        'out',
        style({
          transform: 'translateY(-10%)',
          opacity: 0,
        })
      ),
      transition('in => out', animate('300ms ease-out')),
      transition('out => in', animate('300ms ease-in')),
    ]),
  ],
})
export class SettingsComponent {
  showModal: boolean = false;
  selectedPhoto: string;
  userPhoto = '/assets/photo.jpeg';
  isLogVisible = false;
  user: any;
  logs: any;
  photos: string[] = [
    '/assets/photo(1).jpg',
    '/assets/photo(2).jpg',
    '/assets/photo(3).jpg',
    '/assets/photo(4).jpg',
    '/assets/photo(5).jpg',
    '/assets/photo.jpeg',
  ];
  usernameForm: { [key: string]: string } = {
    username: null,
    email: null,
    password: null,
  };
  passwordForm: { [key: string]: string } = {
    oldPass: null,
    password: null,
    confirmPassword: null,
  };
  deletePassword: string;
  constructor(
    private storageService: StorageService,
    private logService: LogService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    this.user = this.storageService.getUser();
    console.log(this.user.roles);
    if (this.user.roles == null) {
      this.router.navigate(['login']);
    } else {
      this.userPhoto = this.user.pic;
      this.logService
        .getLogs(this.user.username, this.user.roles)
        .subscribe((data) => ((this.logs = data), console.log(data)));
    }
  }

  toggleLogVisibility() {
    this.isLogVisible = !this.isLogVisible;
  }

  openModal(): void {
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
  }

  selectPhoto(photo: string): void {
    this.selectedPhoto = photo;
  }

  confirmSelection(): void {
    if (this.selectedPhoto) {
      this.authService
        .changepicture(this.user.username, this.selectedPhoto)
        .subscribe((user) => this.storageService.saveUser(user));
    }
    this.closeModal();
    this.userPhoto = this.selectedPhoto;
  }

  changeUsername(): void {
    console.log(this.usernameForm['username']);
    this.authService
      .updateusername(
        this.usernameForm['username'],
        this.usernameForm['email'],
        this.usernameForm['password']
      )
      .subscribe((response) => {
        this.storageService.saveUser(response);
        window.location.reload();
      });
  }

  changePassword(): void {
    this.authService
      .updateusername(
        this.passwordForm['oldPass'],
        this.passwordForm['password'],
        this.passwordForm['confirmPassword']
      )
      .subscribe((response) => {
        this.storageService.saveUser(response);
        window.location.reload();
      });
  }

  deleteAccount(): void {}
}
<div class="settings">
  <div class="settings-card">
    <h3>{{ this.user.username }}'s Settings</h3>
  </div>
  <div class="parallel">
    <div class="user-info">
      <div class="user-section">
        <img [src]="userPhoto" alt="profile-picture" (click)="openModal()" />
        <div class="modal-background" *ngIf="showModal">
          <div class="modal-content">
            <button class="close-button" (click)="closeModal()">X</button>
            <!-- Close button -->
            <h3>Select Profile Picture</h3>
            <div class="profile-picture-picker">
              <div
                class="profile-picture"
                [ngClass]="{ selected: selectedPhoto === photo }"
                *ngFor="let photo of photos"
                (click)="selectPhoto(photo)"
              >
                <img [src]="photo" alt="Profile Picture" />
              </div>
            </div>
            <div class="preview-section" *ngIf="selectedPhoto">
              <h3>Preview:</h3>
              <img [src]="selectedPhoto" alt="Selected Profile Picture" />
              <button class="photo-button" (click)="confirmSelection()">
                Confirm
              </button>
            </div>
          </div>
        </div>
      </div>
      <div class="border-left user-section">
        New Username:
        <input
          type="text"
          placeholder="Username"
          name="username"
          [(ngModel)]="usernameForm['username']"
        /><br />
        Email:
        <input
          type="text"
          placeholder="Email"
          name="email"
          [(ngModel)]="usernameForm['email']"
        /><br />
        Password:
        <input
          type="password"
          placeholder="Password"
          name="password"
          [(ngModel)]="usernameForm['password']"
        /><br />
        <button class="edit-email" (click)="changeUsername()">
          Change Username
        </button>
      </div>
      <div class="border-left user-section">
        Old Password:
        <input
          type="password"
          placeholder="Old Password"
          name="oldPassword"
          [(ngModel)]="passwordForm['oldPass']"
        /><br />
        New Password:
        <input
          type="password"
          placeholder="New Password"
          name="newPassword"
          [(ngModel)]="passwordForm['password']"
        /><br />
        Confirm New Password:
        <input
          type="password"
          placeholder="Confrim New Password"
          name="confirmPassword"
          [(ngModel)]="passwordForm['confirmPassword']"
        /><br />
        <button class="password-change" (click)="changePassword()">
          Change password
        </button>
      </div>
    </div>
  </div>
  <div class="settings-card user-logs" (click)="toggleLogVisibility()">
    <h3>
      User Logs
      <i
        [class]="isLogVisible ? 'fa fa-chevron-up' : 'fa fa-chevron-down'"
        aria-hidden="true"
      ></i>
    </h3>
  </div>
  <div *ngIf="isLogVisible" class="logs">
    <div
      [@slideInOut]="isLogVisible ? 'in' : 'out'"
      class="log-card settings-card"
      *ngFor="let log of logs"
    >
      Username:{{ log.username }} Date : {{ log.date }}<br />
      OVERVIEW: "{{ log.language }}: {{ log.log }}"
    </div>
  </div>
  <button class="delete-account" (click)="(deleteAccount)">
    Delete account
  </button>
</div>
