import { Component, OnInit } from '@angular/core';
import { DoctorService } from '../services/doctor.service'; // Create this service to handle communication with the backend

@Component({
  selector: 'app-doctor-interface',
  templateUrl: './doctor-interface.component.html',
  styleUrls: ['./doctor-interface.component.css']
})
export class DoctorInterfaceComponent implements OnInit {
  patients: any[] = []; // Fetch this data from the backend
  selectedPatient: string = '';
  medication: string = '';
  dosage: string = '';

  constructor(private doctorService: DoctorService) {}

  ngOnInit(): void {
    // Fetch the list of patients when the component is initialized
    this.fetchPatients();
  }

  fetchPatients(): void {
    // Use the doctor service to fetch the list of patients from the backend
    this.doctorService.getPatients().subscribe((data: any) => {
      this.patients = data;
    });
  }

  setPrescription(): void {
    // Use the doctor service to send prescription details to the backend
    this.doctorService.setPrescription({
      patient: this.selectedPatient,
      medication: this.medication,
      dosage: this.dosage
    }).subscribe((response: any) => {
      // Handle the response from the backend (success or error)
      console.log(response);
    });
  }
}
