\n}\r\n.extended-input[_ngcontent-%COMP%] {\r\n  display: flex;\r\n  justify-content: center;\r\n  align-items: center;\r\n  width: 70%;\r\n}\r\n.extended[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: 0.465fr 0.5fr 0.1fr 0.5fr;\r\n  grid-gap: 0px;\r\n}\r\n.extended[_ngcontent-%COMP%]   input[_ngcontent-%COMP%] {\r\n  width: 60%;\r\n}\r\n.and[_ngcontent-%COMP%] {\r\n  margin-left: -91%;\r\n}\r\n.extended-input[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  margin-left: 2%;\r\n}\r\n.extended-input[_ngcontent-%COMP%]   input[_ngcontent-%COMP%] {\r\n  width: 70%;\r\n}\r\n\r\n.select-box[_ngcontent-%COMP%]   select[_ngcontent-%COMP%], .select-box-2[_ngcontent-%COMP%]   select[_ngcontent-%COMP%] {\r\n  height: 100%;\r\n  width: 100%;\r\n  outline: none;\r\n  border: none;\r\n  color: #707070;\r\n  font-size: 1rem;\r\n}\r\n\r\n.select-box[_ngcontent-%COMP%]   .ng-dirty[_ngcontent-%COMP%], .select-box-2[_ngcontent-%COMP%]   .ng-dirty[_ngcontent-%COMP%] {\r\n  color: white;\r\n}\r\n.rvr-grid[_ngcontent-%COMP%] {\r\n  display: grid;\r\n  grid-template-columns: repeat(3, 3fr);\r\n  grid-column-gap: -2%;\r\n  grid-row-gap: 0px;\r\n}\r\n.rvr-grid[_ngcontent-%COMP%]   .input-box[_ngcontent-%COMP%] {\r\n  width: 50%;\r\n}\r\n.rvr-grid[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\r\n  width: 127%;\r\n}\r\nbutton[_ngcontent-%COMP%] {\r\n  height: 40px;\r\n  width: 10%;\r\n  margin: 3px;\r\n  color: #eceff1;\r\n  font-size: 1rem;\r\n  font-weight: 400;\r\n  cursor: pointer;\r\n  transition: all 0.2s ease;\r\n  background-color: #455a64;\r\n  border: 1px solid #ddd;\r\n  border-radius: 6px;\r\n  padding: 0 15px;\r\n}\r\n\r\nbutton[_ngcontent-%COMP%]:hover, button[_ngcontent-%COMP%]:focus {\r\n  background-color: #78909c;\r\n}\r\n.broadcast[_ngcontent-%COMP%] {\r\n  float: right;\r\n}\r\n\r\n.broadcast-mode-buttons[_ngcontent-%COMP%] {\r\n  margin-top: 0.5%;\r\n}\r\n\r\nselect[_ngcontent-%COMP%]   option[_ngcontent-%COMP%]:checked {\r\n  color: white;\r\