// doctor-patients-list.component.ts

import { Component, OnInit } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';

@Component({
  selector: 'app-doctor-patients-list',
  templateUrl: './doctor-patients-list.component.html',
  styleUrls: ['./doctor-patients-list.component.css']
})
export class DoctorPatientsListComponent implements OnInit {

  displayedColumns: string[] = ['fullName', 'age', 'sex', 'height', 'weight', 'allergies'];
  patients: any[] = [];
  dataSource: MatTableDataSource<any>;

  constructor(doctorService:DoctorService) {
    // Initialize the data source
    this.dataSource = new MatTableDataSource<any>(this.patients);
  }

  ngOnInit(): void {
    this.getPatients();
  }

  getPatients() {
    // Fetch patient data from the service
    // Example: this.patients = this.patientService.getAllPatients();
    // Update the data source
    this.dataSource.data = this.patients;
  }
}
