import { formatDate } from '@angular/common';
import { Component, Input } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-tabs',
  templateUrl: './tabs.component.html',
  styleUrls: ['./tabs.component.css'],
})
export class TabsComponent {
  message: string;
  nextMessage: string;
  state: boolean;
  broadcastTime: string;
  currentCode: string;
  nextCode: string;
  isRed: boolean = false;

  constructor(private http: HttpClient) {}

  getCurrentCode(currentCode) {
    this.currentCode = currentCode;
    this.addClass();
  }

  getNextCode(nextCode) {
    this.nextCode = nextCode;
  }

  getBroadcastTime(broadcastTime) {
    this.broadcastTime = formatDate(broadcastTime, 'HH:mm', 'en-US', '+0000');
  }

  getCurrentBroadcast(message) {
    this.message = message;
  }

  getSubscribeState(state) {
    this.state = state;
  }

  getNextBroadcast(nextMessage) {
    this.nextMessage = nextMessage;
    console.log(this.nextMessage);
  }

  addClass() {
    if (this.currentCode === 'N/A') {
      this.isRed = true;
    } else {
      this.isRed = false;
    }
  }

  convertTextToSpeech(message: string, language: string): void {
    const requestBody = { text: message, language: language };
    console.log(message);
    this.http
      .post<any>('http://localhost:5000/convert-text-to-speech', requestBody)
      .subscribe({
        next: (response) => {
          const audio = new Audio(
            'http://localhost:5000/assets/current-broadcast.mp3?_=' + Date.now()
          );
          audio.play();
        },
        error: (error) => {
          // Handle any errors
        },
      });
  }
}
<link
  rel="stylesheet"
  href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
/>
<div class="outter-grid">
  <div class="grid">
    <div class="container-title">
      <span title="Florø Airport Norway" class="title">ENFL</span>
      <div [ngClass]="state ? 'led-green' : 'led-red'"></div>
    </div>
    <p class="lastBroadcast">Last broadcast: {{ broadcastTime }}</p>
  </div>
  <div class="current">
    <p class="atis-code" [class.red]="isRed">({{ currentCode }}) (Current)</p>
    <button class="icon-button" (click)="convertTextToSpeech(message, 'en')">
      <i class="fa fa-volume-up fa-2x" aria-hidden="true"></i>
    </button>
  </div>
  <div class="next">
    <p class="atis-code">({{ nextCode }}) (Next)</p>
    <button
      class="icon-button"
      (click)="convertTextToSpeech(nextMessage, 'en')"
    >
      <i class="fa fa-volume-up fa-2x" aria-hidden="true"></i>
    </button>
  </div>
</div>
<mat-tab-group class="custom-tab-group">
  <mat-tab>
    <ng-template mat-tab-label>
      <span class="tabName">ATIS Report </span>
    </ng-template>
    <div class="container">
      <app-atis-form
        (lastBroadcastTime)="getBroadcastTime($event)"
        (isSubscribed)="getSubscribeState($event)"
        (currentBroadcastENGLISH)="getCurrentBroadcast($event)"
        (nextBroadcastENGLISH)="getNextBroadcast($event)"
        (currentAtisCode)="getCurrentCode($event)"
        (nextAtisCode)="getNextCode($event)"
      ></app-atis-form>
    </div>
  </mat-tab>
  <mat-tab>
    <ng-template mat-tab-label>
      <span class="tabName">Overview </span>
    </ng-template>
    <div class="container">
      <app-overview
        [currentBroadcastEN]="message"
        [nextBroadcastEN]="nextMessage"
      ></app-overview>
    </div>
  </mat-tab>
</mat-tab-group>
